import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, View, Button, Alert } from 'react-native';
import * as Font from 'expo-font';
// import { parentPort } from 'worker_threads';
// import { configureFonts, DefaultTheme, Provider as PaperProvider } from 'react-native-paper';
import { Text } from 'react-native-paper';
import { useFonts } from 'expo-font';
// import { render } from '../../Server/api/app';
// import { useFonts, open-sans } from '@expo-google-fonts/open-sans';
// import { useFonts, sans_serif } from '@expo-google-fonts/open-sans';

state = {
  fontsLoaded: false,
}


export default function StudySessionsSettings() {
  let [fontsLoaded] = useFonts({
    'OpenSansBold': require('../assets/fonts/OpenSans-Bold.ttf'),
  });

  if(!fontsLoaded) {
    return null;
  }



    return (
      // <Text>Session Details for Math</Text>
        <View style={styles.container}>
          <Text style={styles.heading}>study session settings</Text>
          {/* <Text style={styles.heading}> text</Text> */}
          <View style={[{margin:10, width:"40%"}]}>
          <Button
            onPress={() => Alert.alert("hello")}
            title="boom pages"
            color="#ab3535"
            accessibilityLabel="Learn more about this purple button"
          />
          <Text style={styles.monday}>Monday</Text>
          <StatusBar style="auto" />
          </View>
        </View>
        );
    }
    
    const styles = StyleSheet.create({
        container: {
          flex: 1,
          backgroundColor: '#33b5e5',
          alignItems: 'center',
          // justifyContent: 'center',
        },
        monday: {
          alignItems: 'flex-start',
          marginRight: '50%',
        },
        heading: {
          fontFamily: 'OpenSansBold',
          fontSize: 60,
          textAlign: 'center',
          marginTop: '30%',
 
        },
      });
